def parse_file(file_path):
    # Create an empty dictionary to store nodes
    nodes = {}

    # Open and read the file
    with open(file_path, 'r') as file:
        for line in file:
            # Split each line into parent and child nodes
            parent, *child_nodes = line.strip().split('->')

            # Concatenate all child nodes into a single string
            if child_nodes:
                child_nodes = '->'.join(child_nodes)

            # Add parent and child nodes to the dictionary
            if parent not in nodes:
                nodes[parent] = [child_nodes] if child_nodes else []
            else:
                nodes[parent].append(child_nodes) if child_nodes else nodes[parent]

    return nodes

