                    for sub_index, sub_item in enumerate(item):
                        if isinstance(sub_item, dict):
                            if 'id' not in sub_item:
                                errors = True
                                print(f"Error: 'id' key is missing in subs[{index}][{sub_index}]")
                                stack.append((f"subs[{index}][{sub_index}]", sub_item))
                        elif isinstance(sub_item, list):
